sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   var whatneeded = g_form.getValue('u_what_needed');

   //dictionary of fieldname/choices that should be removed
   var d = {
	   "legal": ['Legal', 'legal1', 'legal2'],
	   "hr": ['Human Resources', 'hr1', 'hr2'],
	   "facilities": ['Facilities', 'facilities1', 'facilities2'],
   };

   //clear options in "What needed" field
	g_form.clearOptions('u_what_needed');

   //add relevant options
	for (var i = 1; i < d[newValue].length; i++) {
		g_form.addOption('u_what_needed', d[newValue][i], d[newValue][0] + ' ' + i);
	}
	g_form.addOption('u_what_needed', 'other', 'Other');

   //If the form is loading and it is not a new record, set u_what_needed value to value from record before it was loaded
	if (isLoading && !g_form.isNewRecord()) {
		g_form.setValue('u_what_needed', whatneeded);
	}
}
